import { Component, Output, EventEmitter, OnInit } from '@angular/core';
    import { Settings } from '../../interfaces/settings.interface';
    import { StorageService } from '../../services/storage.service';

    @Component({
      selector: 'app-settings',
      templateUrl: './settings.component.html',
      styleUrls: ['./settings.component.css']
    })
    export class SettingsComponent implements OnInit {
      @Output() settingsChange = new EventEmitter<Settings>();
      @Output() textChange = new EventEmitter<string>();
      settings: Settings = {
        horizontalFlip: false,
        verticalFlip: false,
        speed: 'medium',
        font: 'Arial',
        fontSize: 24,
        theme: 'light'
      };
      text: string = '';

      constructor(private storageService: StorageService) { }

      ngOnInit(): void {
        this.loadSettings();
        this.loadText();
      }

      loadSettings() {
        const savedSettings = this.storageService.loadSettings();
        if (savedSettings) {
          this.settings = savedSettings;
          this.applyTheme();
          this.emitSettings();
        }
      }

      loadText() {
        this.text = this.storageService.loadText() || '';
      }

      toggleHorizontalFlip() {
        this.settings.horizontalFlip = !this.settings.horizontalFlip;
        this.saveSettings();
      }

      toggleVerticalFlip() {
        this.settings.verticalFlip = !this.settings.verticalFlip;
        this.saveSettings();
      }

      saveSettings() {
        this.storageService.saveSettings(this.settings);
        this.emitSettings();
      }

      saveText() {
        this.storageService.saveText(this.text);
      }

      emitSettings() {
        this.settingsChange.emit(this.settings);
      }

      onTextChange(text: string) {
        this.textChange.emit(text);
      }

      toggleTheme() {
        this.settings.theme = this.settings.theme === 'light' ? 'dark' : 'light';
        this.applyTheme();
        this.saveSettings();
      }

      applyTheme() {
        document.documentElement.setAttribute('data-theme', this.settings.theme);
      }
    }
<div class="p-4 border-b border-gray-300 flex justify-between items-center">
      <div class="flex space-x-4 items-center">
        <div class="form-control">
          <label class="label">
            <span class="label-text">Font</span>
          </label>
          <select class="select select-bordered w-full max-w-xs" [(ngModel)]="settings.font" (change)="saveSettings()">
            <option value="Arial">Arial</option>
            <option value="Times New Roman">Times New Roman</option>
            <option value="Courier New">Courier New</option>
          </select>
        </div>
        <div class="form-control">
          <label class="label">
            <span class="label-text">Font Size</span>
          </label>
          <select class="select select-bordered w-full max-w-xs" [(ngModel)]="settings.fontSize" (change)="saveSettings()">
            <option value="16">Small</option>
            <option value="24">Medium</option>
            <option value="32">Large</option>
          </select>
        </div>
        <div class="form-control">
          <label class="label">
            <span class="label-text">Speed</span>
          </label>
          <select class="select select-bordered w-full max-w-xs" [(ngModel)]="settings.speed" (change)="saveSettings()">
            <option value="slow">Slow</option>
            <option value="medium">Medium</option>
            <option value="fast">Fast</option>
          </select>
        </div>
      </div>
      <div class="flex space-x-4 items-center">
        <button class="btn" [class.btn-active]="settings.horizontalFlip" (click)="toggleHorizontalFlip()">Horizontal Flip</button>
        <button class="btn" [class.btn-active]="settings.verticalFlip" (click)="toggleVerticalFlip()">Vertical Flip</button>
        <button class="btn" (click)="toggleTheme()">{{ settings.theme === 'light' ? 'Dark Mode' : 'Light Mode' }}</button>
      </div>
    </div>
    <div class="p-4">
      <textarea class="textarea textarea-bordered w-full h-32" placeholder="Enter your text here..." [(ngModel)]="text" (input)="saveText()" (ngModelChange)="onTextChange($event)"></textarea>
    </div>
